import axios from 'axios';


function Card(props) {
    const [data, setData] = useState([]);
    const [image, setImage] = useState();


    useEffect(() => {
        const fetchData = async () => {
            let url = 'https://storage.googleapis.com/wineshop-assets/'
           const result = await axios.get(url +"wine-shop.json"  );
         const response = await axios.get("https://storage.googleapis.com/wineshop-assets/wine-bottles/" + result.data[0].image)



      setData(result.data);
      setImage(response.config.url)

        console.log(response.config.url);


            }
            fetchData()
    }, []);

    return (
        <>
        <div className="flex flex-wrap w-full border border-blue ml-24 mr-4">


        {data.map(item => (

        <div className ="w-1/4  border border-black text-gray-600 m-4 justify-self-auto ">
            <div className="flex">
            <img src ={image} className="h-56 border border-green-500 w-14 m-1"/>
            <div>
            <p className="text-4xl font-serif justify-start border border-black w-40 h-10 mt-1 ">{item.no}</p>
            <p className="text-base font-serif pb-4 border border-black w-40 h-12 mt-1 ">{item.name}</p>

            <div className='flex border border-red-500 mt-4 w-44'>

           <section className=" border border-black mt-0.5 w-20 ml-0.5 mb-1">
            <p className="text-xs font-serif font-medium  text-black"> Bottle</p> 
              <p className="text-xs font-serif  text-black"> $ {item.cost.bottle} </p>

              <input type="checkbox" className="form-checkbox"/>
              <label className="font-serif text-base text-black pl-0.5 ">QTY</label>

             <button className="text-xs w-full text-gray-600  
             bg-gray-300 p-1 block ml-0 ">Details</button>
             </section>

             <section className=" border border-blue-500 mt-0.5  w-20 ml-0.5 mb-1">
            <p className="text-xs font-serif font-medium  text-black"> Case</p> 
              <p className="text-xs font-serif  text-black "> $ {item.cost.case} </p>

              <input type="checkbox" className="form-checkbox"/>
              <label className="font-serif text-base pl-0.5  text-black">QTY</label>

             <button className="text-xs w-full text-gray-200  
             bg-gray-600 p-1 block ml-0 ">Add to cart</button>
             </section>
             </div>

            </div>

